export const useFetch = (id) => {
  const [user, setUser] = useState([]);
  const [activity, setActivity] = useState([]);
  const [sessions, setSessions] = useState([]);
  const [performance, setPerformance] = useState([]);

  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(true);

  useEffect(() => {
    (async () => {
      setError(false);
      try{
        const result = axios.get(`http://localhost:3000/user/12`);
        const result2 = axios.get(`http://localhost:3000/user/12/average-sessions`);
        const result3 = axios.get(`http://localhost:3000/user/12/activity`);
        const result4 = axios.get(`http://localhost:3000/user/12/performance`);
        setUser(result.data);
        setActivity(result2.data);
        setSessions(result3.data);
        setPerformance(result4.data);
      }catch(err){
        setLoading(false);
        setError(err);
        console.log('error true :' , err);
      }
        setLoading(false);
      })();
  }, [id])

  return [user, activity, sessions, performance, loading, error];
}
export default useFetch;
